DataSource.SQL:
select /* LOCATION */ (case when :@WorkstationId is not null then ( select '[' + L.AccountCode + '] ' + L.DisplayName from tbWorkstation W inner join tbAccount L on L.AccountId=W.LocationAccountId where W.WorkstationId=:@WorkstationId ) when :@OpAreaId is not null then ( select '[' + L.AccountCode + '] ' + L.DisplayName from tbAccount OPA inner join tbAccount L on L.AccountId=OPA.ParentAccountId where OPA.AccountId=:@OpAreaId ) when :@LocationId is not null then ( select '[' + L.AccountCode + '] ' + L.DisplayName from tbAccount L where L.AccountId=:@LocationId ) else '' end) as LocationDesc,
/* OPAREA */ (case when :@WorkstationId is not null then ( select '[' + OPA.AccountCode + '] ' + OPA.DisplayName from tbWorkstation W inner join tbAccount OPA on OPA.AccountId=W.OpAreaAccountId where W.WorkstationId=:@WorkstationId ) when :@OpAreaId is not null then ( select '[' + AccountCode + '] ' + DisplayName from tbAccount where AccountId=:@OpAreaId ) else '' end) as OpAreaDesc,
/* WORKSTATION */ (case when :@WorkstationId is not null then ( select ('[' + WorkstationCode + '] ' + WorkstationName) as WorkstationDesc from tbWorkstation where WorkstationId=:@WorkstationId ) else '' end) as WorkstationDesc,
/* USER */ (case when :@UserId is not null then ( select ('[' + LA.UserName + '] ' + A.DisplayName) as UserDesc from tbAccount A left join tbLoginAlias LA on LA.AccountId=A.AccountId and LA.PrimaryAlias=1 where A.AccountId=:@UserId ) else '' end) as UserDesc
