DataSource.SQL:
declare @DateFrom date; set @DateFrom=:@DateFrom;
declare @DateTo date; set @DateTo=:@DateTo;
declare @LocationId uniqueidentifier; set @LocationId=:@LocationId;
declare @OpAreaId uniqueidentifier; set @OpAreaId=:@OpAreaId;
declare @WorkstationId uniqueidentifier; set @WorkstationId=:@WorkstationId;
declare @UserId uniqueidentifier; set @UserId=:@UserId;
declare @PaymentMethodId uniqueidentifier; set @PaymentMethodId=:@Z_PaymentMethodId;
declare @TagId uniqueidentifier; set @TagId=:@Z_TagId;

select
  /* LOCATION */
  (case
    when @OpAreaId is not null   then dbo.fnEntityDesc(6, (select ParentAccountId from tbAccount where AccountId=@OpAreaId))
    when @LocationId is not null then dbo.fnEntityDesc(6, @LocationId)
    else ''
  end) as LocationDesc,

  /* OPAREA */
  (case
    when :@OpAreaId is not null then dbo.fnEntityDesc(14, @OpAreaId)
    else ''
  end) as OpAreaDesc,
  
  /* WORKSTATION */
  (case
    when @WorkstationId is not null then dbo.fnEntityDesc(3, @WorkstationId)
    else ''
  end) as WorkstationDesc,
  
  /* USER */
  (case
    when @UserId is not null
    then (
      select ('[' + LA.UserName + '] ' + A.DisplayName) as UserDesc
      from
        tbAccount A left join 
        tbLoginAlias LA on LA.AccountId=A.AccountId and LA.PrimaryAlias=1
      where A.AccountId=@UserId
    )
    else ''
  end) as UserDesc,
  
  /* PAYMENT METHOD */
  (case
    when @PaymentMethodId is not null then dbo.fnEntityDesc(1004, @PaymentMethodId)
    else ''
  end) as PaymentMethodDesc,
  
  /* TAG */
  (case
    when @TagId is not null
    then (select (TagName) as UserDesc from tbTag where TagId=@TagId)
    else ''
  end) as TagDesc

declare @DateFrom date; set @DateFrom=:@DateFrom; declare @DateTo date; set @DateTo=:@DateTo; declare @LocationId uniqueidentifier; set @LocationId=:@LocationId; declare @OpAreaId uniqueidentifier; set @OpAreaId=:@OpAreaId; declare @WorkstationId uniqueidentifier; set @WorkstationId=:@WorkstationId; declare @UserId uniqueidentifier; set @UserId=:@UserId; declare @PaymentMethodId uniqueidentifier; set @PaymentMethodId=:@Z_PaymentMethodId; declare @TagId uniqueidentifier; set @TagId=:@Z_TagId;
select 1 as GroupDummy, Coalesce(PG.TagName, '-') as PaymentGroup, Coalesce(PM.PluginName, Drv.DriverName) as PaymentMethodName, Sum(P.PaymentAmount) as Amount from tbTransaction T inner join tbPayment P on P.TransactionId=T.TransactionId and P.PaymentStatus=2 /* Approved */ inner join tbPlugin PM on PM.PluginId=P.PluginId inner join tbDriver Drv on Drv.DriverId=PM.DriverId left join tbPluginPaymentMethod PPM on PPM.PluginId=P.PluginId left join tbAccount OPA on OPA.AccountId=T.OpAreaId left join tbTag PG on PG.TagId=PPM.PaymentGroupTagId where T.SerialFiscalDate>=@DateFrom and T.SerialFiscalDate<=@DateTo and (@WorkstationId is null or T.WorkstationId=@WorkstationId) and (@OpAreaId is null or OPA.AccountId=@OpAreaId) and (@LocationId is null or OPA.ParentAccountId=@LocationId) and (@UserId is null or T.UserAccountId=@UserId) and ( @PaymentMethodId is null or exists ( select * from tbPayment XP where XP.TransactionId=T.TransactionId and XP.PluginId=@PaymentMethodId and XP.PaymentStatus=2/*approved*/ ) ) and ( @TagId is null or exists ( select * from tbTransactionItem XTI inner join tbSaleItem XSI on XSI.SaleItemId=XTI.SaleItemId inner join tbTagLink XTGL on XTGL.EntityId=XSI.ProductId and XTGL.TagId=@TagId where XTI.TransactionId=T.TransactionId ) )
group by PG.TagName, PM.PluginName, Drv.DriverName having Sum(P.PaymentAmount)<>0 order by PG.TagName, PM.PluginName
