DataSource.SQL:
declare @DateFrom date; declare @DateTo date;
set @DateFrom=:@DateFrom; set @DateTo=:@DateTo;
select 'T' as [Type (T/I)], convert(varchar, Trn.TransactionFiscalDate, 103) as [Date], SubString(convert(varchar, Trn.TransactionDateTime, 8), 1, 5) as [Time], null as [Sale Item ID], Trn.TransactionId as [Transaction ID], dbo.fnEntityDesc(20,Trn.TransactionId) as [TDSSN], Sal.SaleCode as [Sale Code (PNR)], Loc.AccountCode as [Location Code], Loc.DisplayName as [Location Name], OpA.AccountCode as [Operating Area Code], OpA.DisplayName as [Operating Area Name], Wks.WorkstationCode as [WorkstationCode], Wks.WorkstationName as [WorkstationName], UsrLogin.UserName as [Account Username], UsrEmail.UserName as [Account Username (email)], Usr.DisplayName as [User Account Name], Trn.TransactionSerial as [Transaction Serial], Trn.TransactionType as [Transaction Type Code], dbo.fnLookupItemDesc(3,Trn.TransactionType) as [Transaction Type Label], Trn.Approved as [Approved], Trn.Paid as [Paid], Trn.Encoded as [Encoded], Trn.Printed as [Printed], Trn.Validated as [Validated], '' as [Product Code], '' as [Product Name], 0 as [Quantity], 0 as [Unit Tax], 0 as [Unit Amount], 0 as [Unit Attribute Amount], 0 as [Total Tax], 0 as [Total Amount], Trn.PaidTax as [Paid Tax], Trn.PaidAmount as [Paid Amount], '' as [Print Group], '' as [Attribute Items], '' as [Tags], Stuff(( select '|' + Coalesce(Plg.PluginName, Drv.DriverName) + '=' + Cast(Pay.PaymentAmount as varchar(max)) from tbPayment Pay inner join tbPlugin Plg on Plg.PluginId=Pay.PluginId inner join tbDriver Drv on Drv.DriverId=Plg.DriverId where Pay.TransactionId=Trn.TransactionId and Pay.PaymentStatus=2 --Approved FOR XML PATH('') ), 1, 1, '') + '|' as [Payments], Sal.SaleCode + '=1|' as [REF-PNR], Trn.TransactionSequence as EntityChangeId from tbTransaction Trn inner join tbSale Sal on Sal.SaleId=Trn.SaleId inner join tbWorkstation Wks on Wks.WorkstationId=Trn.WorkstationId left join tbAccount Loc on Loc.AccountId=Wks.LocationAccountId left join tbAccount OpA on OpA.AccountId=Wks.OpAreaAccountId left join tbAccount Usr on Usr.AccountId=Trn.UserAccountId left join tbLoginAlias UsrLogin on UsrLogin.AccountId=Usr.AccountId and UsrLogin.LoginAliasType=0 left join tbLoginAlias UsrEmail on UsrEmail.AccountId=Usr.AccountId and UsrEmail.LoginAliasType=1 where Trn.TransactionFiscalDate>=@DateFrom and Trn.TransactionFiscalDate<=@DateTo and (:@OpAreaId is null or Wks.OpAreaAccountId=:@OpAreaId) and (:@LocationId is null or Wks.LocationAccountId=:@LocationId) and (:@Z_EntityChangeId is null or Trn.TransactionSequence>:@Z_EntityChangeId)
union
select 'I' as [Type (T/I)], convert(varchar, Trn.TransactionFiscalDate, 103) as [Date], SubString(convert(varchar, Trn.TransactionDateTime, 8), 1, 5) as [Time], SI.SaleItemId as [SaleItem ID], Trn.TransactionId as [Transaction ID], dbo.fnEntityDesc(20,Trn.TransactionId) as [TDSSN], Sal.SaleCode as [Sale Code (PNR)], Loc.AccountCode as [Location Code], Loc.DisplayName as [Location Name], OpA.AccountCode as [Operating Area Code], OpA.DisplayName as [Operating Area Name], Wks.WorkstationCode as [Workstation Code], Wks.WorkstationName as [Workstation Name], UsrLogin.UserName as [Account Username], UsrEmail.UserName as [Account Username (email)], Usr.DisplayName as [User Account Name], Trn.TransactionSerial as [Transaction Serial], Trn.TransactionType as [Transaction Type Code], dbo.fnLookupItemDesc(3,Trn.TransactionType) as [Transaction Type Label], Trn.Approved as [Approved], Trn.Paid as [Paid], Trn.Encoded as [Encoded], Trn.Printed as [Printed], Trn.Validated as [Validated], Prd.ProductCode as [Product Code], Prd.ProductName as [Product Name], TI.QtyPaid as [Quantity], SI.UnitTax as [Unit Tax], SI.StatAmount as [Unit Amount], ( select Coalesce(Sum(SI2AI.OptionalPrice), 0) from tbSaleItem2AttributeItem SI2AI where SI2AI.SaleItemId=SI.SaleItemId and SI2AI.Optional<>0 ) as [Unit Attribute Amount], (SI.UnitTax * TI.QtyPaid) as [Total Tax], (SI.StatAmount * TI.QtyPaid) as [Total Amount], 0 as [Paid Tax], 0 as [Paid Amount], PG.TagName as [Print Group], Stuff(( select '|' + AI.AttributeItemName + '=' + Cast(SI2AI.OptionalPrice as varchar(max)) from tbSaleItem2AttributeItem SI2AI inner join tbAttributeItem AI on AI.AttributeItemId=SI2AI.AttributeItemId inner join tbAttribute A on A.AttributeId=AI.AttributeId where SI2AI.SaleItemId=SI.SaleItemId order by A.AttributeWeight, AI.PriorityOrder FOR XML PATH('') ), 1, 1, '') + '|' as [Attribute Items], Stuff(( select '|' + TagName from tbTagLink TL inner join tbTag T on T.TagId=TL.TagId where TL.EntityId=Prd.ProductId order by T.TagName FOR XML PATH('') ), 1, 1, '') + '|' as [Tags], Stuff(( select '|' + Coalesce(Plg.PluginName, Drv.DriverName) + '=' + Cast(Pay.PaymentAmount as varchar(max)) from tbPayment Pay inner join tbPlugin Plg on Plg.PluginId=Pay.PluginId inner join tbDriver Drv on Drv.DriverId=Plg.DriverId where Pay.TransactionId=Trn.TransactionId and Pay.PaymentStatus=2 --Approved FOR XML PATH('') ), 1, 1, '') + '|' as [Payments], (case when Trn.TransactionType in (22,47) then Stuff(( select '|' + X.SaleCode + '=-' + Cast(X.Quantity as varchar(max)) from ( select XSal.SaleCode, Sum(XTck.GroupQuantity) as Quantity from tbEntityLink XTL inner join tbTicket XTck on XTck.TicketId=XTL.DstEntityId inner join tbTransaction XTrn on XTrn.TransactionId=XTck.TransactionId inner join tbSale XSal on XSal.SaleId=XTrn.SaleId where Trn.TransactionType in (22,47) and XTL.SrcEntityId=Trn.TransactionId and XTck.ProductId=SI.ProductId group by XSal.SaleCode ) X FOR XML PATH('') ), 1, 1, '') + '|' else Sal.SaleCode + '=' + Cast(TI.Quantity as varchar(max)) + '|' end) as [REF-PNR], Trn.TransactionSequence as EntityChangeId from tbTransaction Trn inner join tbSale Sal on Sal.SaleId=Trn.SaleId left join tbWorkstation Wks on Wks.WorkstationId=Trn.WorkstationId left join tbAccount Loc on Loc.AccountId=Wks.LocationAccountId left join tbAccount OpA on OpA.AccountId=Wks.OpAreaAccountId left join tbAccount Usr on Usr.AccountId=Trn.UserAccountId left join tbTransactionItem TI on TI.TransactionId=Trn.TransactionId left join tbSaleItem SI on SI.SaleItemId=TI.SaleItemId left join tbProduct Prd on Prd.ProductId=SI.ProductId left join tbTag PG on PG.TagId=Prd.PrintGroupTagId left join tbLoginAlias UsrLogin on UsrLogin.AccountId=Usr.AccountId and UsrLogin.LoginAliasType=0 left join tbLoginAlias UsrEmail on UsrEmail.AccountId=Usr.AccountId and UsrEmail.LoginAliasType=1 where Trn.TransactionFiscalDate>=@DateFrom and Trn.TransactionFiscalDate<=@DateTo and (:@OpAreaId is null or Wks.OpAreaAccountId=:@OpAreaId) and (:@LocationId is null or Wks.LocationAccountId=:@LocationId) and (:@Z_EntityChangeId is null or Trn.TransactionSequence>:@Z_EntityChangeId) order by Trn.TransactionSequence
