DataSource.SQL:
declare @DateFrom date; declare @DateTo date;
set @DateFrom=:@DateFrom; set @DateTo=:@DateTo;
select convert(varchar, Trn.TransactionFiscalDate, 103) as [Date], SubString(convert(varchar, Trn.TransactionDateTime, 8), 1, 5) as [Time], SI.SaleItemId as [Sale Item ID], Trn.TransactionId as [Transaction ID], Sal.SaleCode as [Sale Code (PNR)], Acc.AccountId as [Client ID], Acc.AccountCode as [Client Code], ClientLogin.UserName as [Client Username], ClientEmail.UserName as [Client Username (email)], Acc.DisplayName as [Client Name], Stuff(( select ', ' + CAT.CategoryName from tbCategory2Entity C2E inner join tbCategory CAT on CAT.CategoryId=C2E.CategoryId where C2E.EntityId=Acc.AccountId order by CAT.CategoryName FOR XML PATH('') ), 1, 2, '') as [Client Category], SC.SaleChannelCode as [Sale Channel Code], SC.SaleChannelName as [Sale Channel Name], SalLoc.AccountCode as [Order Location Code], SalLoc.DisplayName as [Order Location Name], SalOpA.AccountCode as [Order Operating Area Code], SalOpA.DisplayName as [Order Operating Area Name], SalWks.WorkstationCode as [Order Workstation Code], SalWks.WorkstationName as [Order Workstation Name], SalLogin.UserName as [Order Account Username], SalEmail.UserName as [Order Account Username (email)], SalUsr.DisplayName as [Order User Account Name], TrnLoc.AccountCode as [Transaction Location Code], TrnLoc.DisplayName as [Transaction Location Name], TrnOpA.AccountCode as [Transaction Operating Area Code], TrnOpA.DisplayName as [Transation Operating Area Name], TrnWks.WorkstationCode as [Transaction Workstation Code], TrnWks.WorkstationName as [Transaction Workstation Name], TrnLogin.UserName as [Transaction Account Username], TrnEmail.UserName as [Transaction Account Username (email)], TrnUsr.DisplayName as [Transaction User Account Name], Trn.TransactionSerial as [Transaction Serial], Trn.TransactionType as [Transaction Type Code], dbo.fnLookupItemDesc(3,Trn.TransactionType) as [Transaction Type Label], Trn.Approved as [Approved], Trn.Paid as [Paid], Trn.Encoded as [Encoded], Trn.Printed as [Printed], Trn.Validated as [Validated], Prd.ProductCode as [Product Code], Prd.ProductName as [Product Name], DepAcc.AccountId as [Deposit Account ID], DepAcc.AccountCode as [Deposit Account Code], DepAcc.DisplayName as [Deposit Account Name], TI.QtyPaid as [Quantity], SI.UnitTax as [UnitTax], SI.StatAmount as [Unit Amount], ( select Coalesce(Sum(SI2AI.OptionalPrice), 0) from tbSaleItem2AttributeItem SI2AI where SI2AI.SaleItemId=SI.SaleItemId and SI2AI.Optional<>0 ) as [Unit Attribute Amount], (SI.UnitTax * TI.QtyPaid) as [Total Tax], (SI.StatAmount * TI.QtyPaid) as [Total Amount], Trn.PaidAmount as [Paid Amount], Trn.PaidTax as [Paid Tax], PG.TagName as [Print Group], FG.TagName as [Finance Group], AG.TagName as [Admission Group], RG.TagName as [Area Group], Stuff(( select '|' + AI.AttributeItemName + '=' + Cast(SI2AI.OptionalPrice as varchar(max)) from tbSaleItem2AttributeItem SI2AI inner join tbAttributeItem AI on AI.AttributeItemId=SI2AI.AttributeItemId inner join tbAttribute A on A.AttributeId=AI.AttributeId where SI2AI.SaleItemId=SI.SaleItemId order by A.AttributeWeight, AI.PriorityOrder FOR XML PATH('') ), 1, 1, '') + '|' as [Attribute Items], Stuff(( select '|' + TagName from tbTagLink TL inner join tbTag T on T.TagId=TL.TagId where TL.EntityId=Prd.ProductId order by T.TagName FOR XML PATH('') ), 1, 1, '') + '|' as [Tags], Stuff(( select '|' + Coalesce(Plg.PluginName, Drv.DriverName) + (case when PayCard.AuthorizationCode is not null then '#AuthCode_' + PayCard.AuthorizationCode when Pay.PaymentType=6/*voucher*/ then '#AccountCode_' + VouAccount.AccountCode + '#VoucherCode_' + Coalesce(Vou.VoucherCode, '') else '' end) + '=' + Cast(Pay.PaymentAmount as varchar(max)) from tbPayment Pay WITH(INDEX(IX_Payment_TransactionId)) inner join tbPlugin Plg on Plg.PluginId=Pay.PluginId inner join tbDriver Drv on Drv.DriverId=Plg.DriverId left join tbPaymentCard PayCard on PayCard.PaymentId=Pay.PaymentId left join tbPaymentCredit PayCredit on PayCredit.PaymentId=Pay.PaymentId left join tbVoucher Vou on Vou.VoucherId=PayCredit.VoucherId left join tbAccount VouAccount on VouAccount.AccountId=Vou.AccountId where Pay.TransactionId=Trn.TransactionId and Pay.PaymentStatus=2 --Approved FOR XML PATH('') ), 1, 1, '') + '|' as [Payments], Trn.TransactionSequence as EntityChangeId from tbTransaction Trn WITH(INDEX(IX_Transaction_FiscalDate)) left join tbSale Sal WITH(FORCESEEK) on Sal.SaleId=Trn.SaleId left join tbSaleChannel SC WITH(FORCESEEK) on SC.SaleChannelId=Sal.SaleChannelId left join tbSale2Account S2A WITH(FORCESEEK) on S2A.SaleId=Sal.SaleId and S2A.SaleAccountType=1 left join tbAccount Acc WITH(FORCESEEK) on Acc.AccountId=S2A.AccountId left join tbWorkstation SalWks WITH(FORCESEEK) on SalWks.WorkstationId=Sal.WorkstationId left join tbAccount SalLoc WITH(FORCESEEK) on SalLoc.AccountId=SalWks.LocationAccountId left join tbAccount SalOpA WITH(FORCESEEK) on SalOpA.AccountId=SalWks.OpAreaAccountId left join tbAccount SalUsr WITH(FORCESEEK) on SalUsr.AccountId=Sal.UserAccountId left join tbWorkstation TrnWks WITH(FORCESEEK) on TrnWks.WorkstationId=Trn.WorkstationId left join tbAccount TrnLoc WITH(FORCESEEK) on TrnLoc.AccountId=TrnWks.LocationAccountId left join tbAccount TrnOpA WITH(FORCESEEK) on TrnOpA.AccountId=TrnWks.OpAreaAccountId left join tbAccount TrnUsr WITH(FORCESEEK) on TrnUsr.AccountId=Trn.UserAccountId left join tbTransactionItem TI WITH(FORCESEEK) on TI.TransactionId=Trn.TransactionId and TI.QtyPaid<>0 left join tbSaleItem SI WITH(FORCESEEK) on SI.SaleItemId=TI.SaleItemId left join tbSaleItemDepositAccount SIDA WITH(FORCESEEK) on SIDA.SaleItemId=SI.SaleItemId left join tbAccount DepAcc WITH(FORCESEEK) on DepAcc.AccountId=SIDA.AccountId left join tbProduct Prd WITH(FORCESEEK) on Prd.ProductId=SI.ProductId left join tbTag PG WITH(FORCESEEK) on PG.TagId=Prd.PrintGroupTagId left join tbTag FG WITH(FORCESEEK) on FG.TagId=Prd.FinanceGroupTagId left join tbTag AG WITH(FORCESEEK) on AG.TagId=Prd.AdmGroupTagId left join tbTag RG WITH(FORCESEEK) on RG.TagId=Prd.AreaGroupTagId left join tbLoginAlias ClientLogin WITH(FORCESEEK) on ClientLogin.AccountId=Acc.AccountId and ClientLogin.LoginAliasType=0 and ClientLogin.PrimaryAlias=1 left join tbLoginAlias ClientEmail WITH(FORCESEEK) on ClientEmail.AccountId=Acc.AccountId and ClientEmail.LoginAliasType=1 left join tbLoginAlias SalLogin WITH(FORCESEEK) on SalLogin.AccountId=SalUsr.AccountId and SalLogin.LoginAliasType=0 and SalLogin.PrimaryAlias=1 left join tbLoginAlias SalEmail WITH(FORCESEEK) on SalEmail.AccountId=SalUsr.AccountId and SalEmail.LoginAliasType=1 left join tbLoginAlias TrnLogin WITH(FORCESEEK) on TrnLogin.AccountId=TrnUsr.AccountId and TrnLogin.LoginAliasType=0 and TrnLogin.PrimaryAlias=1 left join tbLoginAlias TrnEmail WITH(FORCESEEK) on TrnEmail.AccountId=TrnUsr.AccountId and TrnEmail.LoginAliasType=1 where Trn.TransactionFiscalDate>=@DateFrom and Trn.TransactionFiscalDate<=@DateTo and Trn.Paid=1 and (:@OpAreaId is null or TrnWks.OpAreaAccountId=:@OpAreaId) and (:@LocationId is null or TrnWks.LocationAccountId=:@LocationId) and (:@Z_EntityChangeId is null or Trn.TransactionSequence>:@Z_EntityChangeId) order by Trn.TransactionSequence
