DataSource.SQL:
declare @AccountId uniqueidentifier; declare @AccountCode varchar(15);
if (len(:@AccountId)=36) begin select @AccountCode=AccountCode from tbAccount where AccountId=:@AccountId end else begin select @AccountCode=:@AccountId end
select AD.CompanyName as [Company Name], V.VoucherName as [Voucher Name], V.VoucherCode as [Voucher Code], V.AmountMax as [Issued Price], Stuff(( select ', ' + CAT.CategoryName from tbCategory2Entity C2E inner join tbCategory CAT on CAT.CategoryId=C2E.CategoryId where C2E.EntityId=A.AccountId order by CAT.CategoryName FOR XML PATH('') ), 1, 2, '') as [Category Names], V.CreateDate as [Creation Date], V.ValidDateTo as [Expiration Date], Convert(DATE, (select T.TransactionDateTime from tbTransactionItem TI inner join tbTransaction T on T.TransactionId=TI.TransactionId and T.TransactionType=44 where SaleItemId in (select top(1) SaleItemId from tbSaleItemVoucher where VoucherId=V.VoucherId) )) as [Redemption Date], V.VoucherDescription as [Voucher Description] from tbVoucher V left join tbAccount A on A.AccountId=V.AccountId left join tbAccountDetail AD on AD.AccountId=A.AccountId where V.CreateDate>=:@DateFrom and V.CreateDate<=:@DateTo and Coalesce(@AccountCode, '') in ('', AccountCode) order by AD.CompanyName, V.VoucherName, V.VoucherCode
